$IEFav =  [Environment]::GetFolderPath('Favorites','None')  $Shell = New-Object -ComObject WScript.Shell $IEFav = Join-Path -Path $IEFav -ChildPath 'Links' $Name = 'Tenant Portal' $FullPath = Join-Path -Path $IEFav -ChildPath "$($Name).url"$DesktopPath = $env:USERPROFILE + "\Desktop\$($Name).url" $url = 'https://portal.local.azurestack.external/' $shortcut = $Shell.CreateShortcut($FullPath)$shortcut.TargetPath = $Url$shortcut.Save()$shortcut = $Shell.CreateShortcut($DesktopPath)$shortcut.TargetPath = $Url$shortcut.Save()$IEFav =  [Environment]::GetFolderPath('Favorites','None')  $Shell = New-Object -ComObject WScript.Shell $IEFav = Join-Path -Path $IEFav -ChildPath 'Links' $Name = 'Admin Portal' $FullPath = Join-Path -Path $IEFav -ChildPath "$($Name).url"$DesktopPath = $env:USERPROFILE + "\Desktop\$($Name).url" $url = 'https://adminportal.local.azurestack.external/' $shortcut = $Shell.CreateShortcut($FullPath)$shortcut.TargetPath = $Url$shortcut.Save()$shortcut = $Shell.CreateShortcut($DesktopPath)$shortcut.TargetPath = $Url$shortcut.Save()